// Lookup helpers
export function getBookById(bookId: string): Book | undefined {
  return books.find(b => b.id === bookId);
}

export function getSongById(songId: string): { song: Song; book: Book } | undefined {
  for (const book of books) {
    const song = book.songs.find(s => s.id === songId);
    if (song) return { song, book };
  }
  return undefined;
}

export function getBooksByLevel(level: string): Book[] {
  return books.filter(b => b.level === level);
}
